mapper.insert:

	   SELECT (IFNULL(MAX(SN), 0)+1) sn from CAMERA		   


    	INSERT INTO CAMERA 
			( SN
			  , UUID
			  , PHOTO_SJ
			  , FILE_SN 
			  , USEYN)
		VALUES ( #{sn}
			  , #{uuid}
			  , #{photoSj}
			  , #{fileSn}
			  , #{useyn})


	   SELECT (IFNULL(MAX(FILE_SN), 0)+1) fileSn from FILE_DETAIL_INFO		   


    	INSERT INTO FILE_DETAIL_INFO
			( FILE_SN
			  , FILE_STRE_COURS
			  , STRE_FILE_NM
			  , ORIGNL_FILE_NM
			  , FILE_EXTSN
			  , FILE_CN
			  , FILE_SIZE )
		VALUES ( #{fileSn}
			  , #{fileStreCours}
			  , #{streFileNm}
			  , #{orignlFileNm}
			  , #{fileExtsn}
			  , #{fileCn}
			  , #{fileSize})

mapper.update:

			UPDATE	CAMERA SET
				  FILE_SN = #{fileSn}
 			WHERE  SN = #{sn}
