mapper.insert:



SELECT (IFNULL(MAX(ID),0)+1) FROM CONTACT_INFO WHERE UUID = #{uuid}


INSERT INTO CONTACT_INFO
	( UUID
	  , ID 
	  , NAME
	  , TELNO
	  , EMAILS				  
	  , USEYN )
VALUES ( #{uuid}
	  , #{contactId}
	  , #{name}
	  , #{telNo}
	  , #{emails}				  
	  , #{useYn})

mapper.update:


UPDATE CONTACT_INFO 
SET ID=#{newId} 
	, NAME=#{name}
	, TELNO=#{telNo}
	, EMAILS=#{emails}				
	, USEYN=#{useYn}
WHERE UUID=#{uuid} AND NAME=#{name} AND TELNO=#{telNo}

mapper.select:
SELECT A.UUID, A.ID AS contactId, A.NAME, A.TELNO, A.EMAILS, A.USEYN
FROM ( 
	SELECT UUID, ID, TRIM(NAME) AS NAME, TRIM(REPLACE(TELNO,'-','')) AS TELNO, EMAILS, USEYN
	FROM CONTACT_INFO
	WHERE USEYN='Y' AND UUID=#{uuid}
) A
WHERE A.UUID=#{uuid} AND A.NAME=#{name} AND A.TELNO=#{telNoCompare}

SELECT COUNT(*) totcnt
FROM CONTACT_INFO
WHERE UUID=#{uuid}
