mapper.insert:

   SELECT (NVL(MAX(SN), 0)+1) sn from DEVICE



	INSERT INTO DEVICE 
		( SN
		  , UUID
		  , OS
		  , TELNO
		  , STRGE_INFO
		  , NTWRK_DEVICE_INFO
		  , PG_VER
		  , DEVICE_NM
		  , USEYN )
	VALUES ( #{sn}
		  , #{uuid}
		  , #{os}
		  , #{telno}
		  , #{strgeInfo}
		  , #{ntwrkDeviceInfo}
		  , #{pgVer}
		  , #{deviceNm}
		  , 'Y')

mapper.update:

UPDATE DEVICE 
SET SN=#{sn}
	, UUID=#{uuid}
	, OS=#{os}
	, TELNO=#{telno}
	, STRGE_INFO=#{strgeInfo}
	, NTWRK_DEVICE_INFO=#{ntwrkDeviceInfo}
	, PG_VER=#{pgVer}
	, DEVICE_NM=#{deviceNm}
	, USEYN=#{useYn}
WHERE SN=#{sn}

mapper.delete:

UPDATE DEVICE 
SET USEYN='N'
WHERE SN=#{sn}

mapper.select:

SELECT COUNT(*) totcnt
FROM DEVICE
WHERE USEYN='Y' AND UUID=#{uuid}
