mapper.insert:


INSERT INTO CONTACT_INFO
	( UUID
	  , ID 
	  , NAME
	  , TELNO
	  , EMAILS				  
	  , USEYN )
VALUES ( #{uuid}
	  , #{contactId}
	  , #{name}
	  , #{telNo}
	  , #{emails}				  
	  , #{useYn})

mapper.update:


UPDATE CONTACT_INFO 
SET ID=#{newId} 
WHERE UUID=#{uuid} AND ID=#{contactId}

mapper.select:
SELECT COUNT(*) totcnt
FROM CONTACT_INFO
WHERE UUID=#{uuid}
