mapper.insert:

   SELECT (IFNULL(MAX(SN), 0)+1) sn from NETWORK



	INSERT INTO NETWORK 
		( SN
		  , UUID
		  , NTWRK_TYPE
		  , USEYN )
	VALUES ( #{sn}
		  , #{uuid}
		  , #{networktype}
		  , #{useYn})

mapper.update:

UPDATE NETWORK 
SET SN=#{sn}
	, UUID=#{uuid}
	, NTWRK_TYPE=#{networktype}
	, USEYN=#{useYn}
WHERE SN=#{sn}

mapper.select:
SELECT COUNT(*) totcnt
FROM NETWORK
WHERE USEYN='Y'
AND UUID = #{uuid}
AND NTWRK_TYPE = #{networktype}
